import java.io.BufferedReader;import java.io.BufferedWriter;import java.io.InputStreamReader;import java.io.OutputStreamWriter;import java.net.Socket;import java.net.URLEncoder;import java.nio.charset.StandardCharsets;public class POSTHTTP {    public static void main(String[] args) throws Exception{        Socket socket = new Socket("testing-ground.scraping.pro", 80);        BufferedWriter wr = new BufferedWriter(new OutputStreamWriter(socket.getOutputStream(), StandardCharsets.US_ASCII));        String data = URLEncoder.encode("usr", "ASCII") + "=" + URLEncoder.encode("admin", "ASCII") + "&" +                URLEncoder.encode("pwd", "ASCII") + "=" + URLEncoder.encode("12345", "ASCII");        wr.write("POSTHTTP /login?mode=login HTTP/1.1\r\n");        wr.write("Host: testing-ground.scraping.pro\r\n");        wr.write("Content-Length: " + data.length() + "\r\n");        wr.write("Content-Type: application/x-www-form-urlencoded\r\n");        wr.write("\r\n");        wr.write(data);        wr.flush();        BufferedReader rd = new BufferedReader(new InputStreamReader(socket.getInputStream()));        String line;        while ((line = rd.readLine()) != null) {            System.out.println(line);        }        wr.close();        rd.close();    }}